mapper.insert:
INSERT INTO course
SET user_id= #{userId}
,classroom = #{classroom}
,course_code = #{courseCode}
,semester_id = #{semesterId}
,type = #{type}
,time = #{time}
,title = #{title}
,credit = #{credit}
,week_plan = #{weekPlan}
,text_book = #{textBook}
,goal = #{goal}
,max_std = #{maxStd}
,rem_std = #{maxStd}
,grade = #{grade}
,status = '처리중'
,created_at = NOW()

mapper.select:
SELECT e.user_id, e.enrollment_id
FROM enrollment e
WHERE e.course_id = #{courseId}

SELECT review, ev_score
FROM enrollment
WHERE course_id = #{courseId}

mapper.update:
UPDATE enrollment
SET review = #{review},
ev_score = #{evScore},
status = '수강완료'
WHERE course_id = #{courseId}
AND user_id = #{userId}

UPDATE enrollment
SET status = #{status}
WHERE course_id = #{courseId} AND user_id = #{userId}

mapper.delete:
DELETE FROM course
WHERE course_id = #{courseId}
AND user_id = #{userId}
